mapper.update:
update act_info
set act_name = #{actName,jdbcType=VARCHAR},
  start_time = #{startTime,jdbcType=TIMESTAMP},
  end_time = #{endTime,jdbcType=TIMESTAMP},
  create_time = #{createTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select count(1) from act_info

  1=1

   and id = #{id}


   and act_name = #{actName}

  and is_deleted = 0

select ai.id, ai.act_name, ai.act_status, ai.start_time, ai.end_time, ai.main_pic, ai.act_url, ai.is_deleted, ai.create_time, ai.type, (select count(1) from vote v where ai.id = v.act_id) as vote_num, (select count(1) from act_shop asp where ai.id = asp.act_id) as shop_num, (select count(1) from access_log al where ai.id = al.ref_id and al.biz_type = 2) as view_num from act_info ai
1=1
and ai.id = #{id}
and ai.act_name = #{actName}
and is_deleted = 0
order by ai.create_time desc limit #{start}, #{rows}
