mapper.update:
update act_shop
set act_id = #{actId,jdbcType=BIGINT},
  shop_id = #{shopId,jdbcType=BIGINT},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  update_time = #{updateTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
SELECT a.id, a.act_id, a.shop_id, a.shop_code, a.apply_status, a.create_time, a.update_time, s.shop_name, s.shop_info, s.logo_url, (select count(1) from vote where shop_id = s.id) as vote_num, (select count(1) from vote where shop_id = s.id
AND user_id = #{voteUserId}
) as is_vote FROM act_shop a inner join shop s on a.shop_id = s.id
1=1
and a.id = #{id,jdbcType=BIGINT}
AND a.act_id = #{actId,jdbcType=BIGINT}
and (s.shop_name like concat('%', #{searchKey,jdbcType=VARCHAR}, '%') or a.shop_code = #{searchKey,jdbcType=VARCHAR})
and a.apply_status = 1 and a.is_luoxuan = 0
ORDER by a.vote_num DESC limit #{start}, #{rows}

SELECT a.id, a.act_id, a.shop_id, a.shop_code, a.apply_status, a.create_time, a.update_time, a.vote_num, a.is_luoxuan, b.act_name, b.act_status, b.act_desc, b.start_time, b.end_time, b.main_pic, b.act_url, b.is_deleted, (select count(1) from vote where act_id = a.act_id
AND user_id = #{voteUserId}
) as is_vote FROM act_shop a INNER JOIN act_info b on a.act_id = b.id
1=1
and a.id = #{id,jdbcType=BIGINT}
AND a.shop_id = #{shopId,jdbcType=BIGINT}
and a.apply_status = 1 and a.is_luoxuan = 0

select

from act_shop

    1=1

       and id = #{id}


        and act_id = #{actId}


        and shop_id = #{shopId}

    and apply_status != 2
    and is_luoxuan = 0
