mapper.update:
update act_user set is_luoxuan =1 WHERE apply_status=1 AND id NOT IN
#{item.id,jdbcType=BIGINT}
AND act_id IN
#{item.actId,jdbcType=BIGINT}

mapper.select:
select a.id, a.act_id, a.user_id, a.user_code, a.act_user_name, a.pic_ids, a.apply_status,a.vote_num, a.create_time, a.update_time, u.icon_url, u.age, u.height, (select count(1) from vote where player_user_id = a.user_id
AND user_id = #{voteUserId} AND (create_time BETWEEN #{beginTime} and #{endTime})
) as is_vote from act_user a LEFT join user u on a.user_id = u.id
1=1
and a.id = #{id}
and a.act_id = #{actId}
and a.user_id = #{userId}
and a.apply_status != 2 and a.is_luoxuan = 0
