mapper.select:
SELECT c.id, c.so_id, c.coupon_code, c.sku_id, c.sku_code, c.sku_name, c.coupon_amount, c.type, c.qrcode_url, c.user_id, c.user_name, c.is_used, c.is_deleted, c.expiry_date, c.create_time, c.used_time, c.shop_id, c.shop_cashier_id, c.update_time, ' ' as rule_desc FROM coupon c
c.id = #{id,jdbcType=BIGINT}
and c.coupon_code = #{couponCode,jdbcType=VARCHAR}
and c.sku_id = #{skuId,jdbcType=BIGINT}
and c.sku_code = #{skuCode,jdbcType=VARCHAR}
and c.user_id = #{userId,jdbcType=BIGINT}
and c.user_name = #{userName,jdbcType=VARCHAR}
and c.is_used = 0
and (c.is_used = 1 or (c.is_used = 0 and c.expiry_date < now()))
order by c.id desc limit #{start}, #{rows}

select count(1)
from coupon


    id = #{id,jdbcType=BIGINT}


    and coupon_code = #{couponCode,jdbcType=VARCHAR}


    and sku_id = #{skuId,jdbcType=BIGINT}


    and sku_code = #{skuCode,jdbcType=VARCHAR}


    and user_id = #{userId,jdbcType=BIGINT}


    and user_name = #{userName,jdbcType=VARCHAR}


    and is_used = 0


     and (is_used = 1 or (is_used = 0 and expiry_date < now()))
