mapper.select:
select

from sku
where sku_type = #{skuType,jdbcType=INTEGER}
and sku_name = #{skuName,jdbcType=VARCHAR}

  and sell_price = #{sellPrice,jdbcType=DECIMAL}


  and is_deleted = #{isDeleted,jdbcType=INTEGER}

SELECT sku.id, sku.sku_code, sku.sku_name, sku.sku_type, sku.sell_price, sku.in_price, sku.agio_price, sku.stock_num, sku.pic_url, sku.create_time, sku.update_time, sku.opreator_id, sku.opreator_name, sku.sku_info, ' ' as rule_desc, cs.amount, (select count(1) from coupon where sku_id = sku.id) sell_num
,(select COUNT(1) from so s LEFT JOIN so_detail sw on s.id = sw.so_id where sw.sku_id = #{id,jdbcType=BIGINT} and s.so_status = 2 and s.user_id = #{userId,jdbcType=BIGINT}) as is_agio ,(SELECT count(1) FROM so_detail sd INNER JOIN so s ON sd.so_id = s.id WHERE s.user_id = #{userId,jdbcType=BIGINT} AND sd.sku_id = #{id,jdbcType=BIGINT} AND (TO_DAYS(sd.create_time) = TO_DAYS(now()))) as is_act
FROM sku sku LEFT JOIN coupon_sku cs on sku.id = cs.sku_id
1=1
and sku.id = #{id,jdbcType=BIGINT}
and sku.sku_code = #{skuCode,jdbcType=VARCHAR}
and sku.sku_name like concat('%', #{skuName,jdbcType=VARCHAR}, '%')
and sku.sku_type = #{skuType,jdbcType=INTEGER}
and sku.is_deleted = 0
order by sku.sell_price ASC limit #{start}, #{rows}

select count(1)
from sku

  1=1

   and id = #{id,jdbcType=BIGINT}


    and sku_code = #{skuCode,jdbcType=VARCHAR}


    and sku_name like concat('%', #{skuName,jdbcType=VARCHAR}, '%')


    and sku_type = #{skuType,jdbcType=INTEGER}

  and sku.is_deleted = 0

select sku.id, sku.sku_code, sku.sku_name, sku.sku_type, sku.sell_price, sku.in_price, sku.agio_price, sku.stock_num, sku.pic_url, sku.create_time, sku.update_time, sku.opreator_id, sku.opreator_name, sku.sku_info, (select count(1) from zan z where ref_id = sku.id and z.type = 6) as zan, (select count(1) from zan z where ref_id = sku.id and z.type = 6
AND user_id = #{zanUserId}
) as is_zan, round((sku.agio_price * 10)/sku.sell_price,1) as agio_level from sku sku inner join sku_shop ss on sku.id = ss.sku_id
1=1
and sku.id = #{id,jdbcType=BIGINT}
and sku.sku_code = #{skuCode,jdbcType=VARCHAR}
and sku.sku_name like concat('%', #{skuName,jdbcType=VARCHAR}, '%')
and sku.sku_type = #{skuType,jdbcType=INTEGER}
and ss.shop_id = #{shopId,jdbcType=BIGINT}
and sku.is_deleted = 0
order by id desc

select sku.id, sku.sku_code, sku.sku_name from sku sku inner join sku_shop ss on sku.id = ss.sku_id
1=1
and sku.id = #{id,jdbcType=BIGINT}
and sku.sku_code = #{skuCode,jdbcType=VARCHAR}
and sku.sku_name like concat('%', #{skuName,jdbcType=VARCHAR}, '%')
and sku.sku_type = #{skuType,jdbcType=INTEGER}
and ss.shop_id = #{shopId,jdbcType=BIGINT}
and sku.is_deleted = 0
order by id desc

select sku.id, sku.sku_code, sku.sku_name, sku.sku_type, sku.sell_price, sku.in_price, sku.agio_price, sku.stock_num, sku.pic_url, sku.create_time, sku.update_time, sku.opreator_id, sku.opreator_name, sku.sku_info, (select count(1) from zan z where ref_id = sku.id and z.type = 6) as zan, (select count(1) from zan z where ref_id = sku.id and z.type = 6
AND user_id = #{zanUserId}
) as is_zan, round((sku.agio_price * 10)/sku.sell_price,1) as agio_level
,(select count(1) from act_sku ak where sku_id = sku.id and act_id = #{actId,jdbcType=BIGINT} and apply_status = 1 and is_luoxuan = 0) as is_sign
from sku sku inner join sku_shop ss on sku.id = ss.sku_id
1=1
and sku.id = #{id,jdbcType=BIGINT}
and sku.sku_code = #{skuCode,jdbcType=VARCHAR}
and sku.sku_name like concat('%', #{skuName,jdbcType=VARCHAR}, '%')
and sku.sku_type = #{skuType,jdbcType=INTEGER}
and ss.shop_id = #{shopId,jdbcType=BIGINT}
and sku.is_deleted = 0
order by id desc limit #{start}, #{rows}

