mapper.select:
select

from user


          id=#{id}


          and user_name = #{userName}


        and source_type = #{sourceType}


        and nick_name like concat('%',#{nickName},'%')


  limit #{start}, #{rows}

select count(1) from user


        id=#{id}


        and user_name = #{userName}


      and source_type = #{sourceType}


      and nick_name like concat('%',#{nickName},'%')

select

from user
where user_name = #{userName}
  and password = #{password}
limit 1
